mapper.select:
select head_id , head
from treeinfo 
group by head;

select head_id , head , dept_id ,depart
from treeinfo 
group by depart;

select  dept_id ,depart,team_id , team 
from treeinfo 
group by team;

mapper.update:
UPDATE user
SET user_name = #{user_name} 
,pos_id = #{pos_id}
,birth_date=#{birth_date}  
,sex= #{sex} 
,email=#{email}  
,phone_number=#{phone_number} 
,flag = #{flag}

WHERE user_id =#{user_id}  ;
