mapper.select:
select * 
from user_detail
where dept_name = (select distinct dept_name from user_detail where user_id = #{user_id}) and duty_id = 'd02';

select * 
from user_detail
where dept_name = (select distinct upper_dept from user_detail where user_id =#{user_id}) and duty_id = 'd03';

select * 
from user_detail
where dept_name = (select distinct upper_dept2 from user_detail where user_id =#{user_id}) and duty_id = 'd04';
